import {Meteor} from 'meteor/meteor';
import {Players} from './../imports/api/players';

Meteor.startup(() => {
    class Person {
        constructor(name = 'Anonymous', age = 0){
            this.name = name;
            this.age = age;
        }
        getGreeting(){
            return `Hi! ${this.name}.`;
        }
        getPersonDescription(){
            return `${this.name} is ${this.age} year(s) old.`;
        }
    }

    class Employee extends Person {
        constructor(name,age,title){
            super(name,age);
            this.title = title;
        }
        getGreeting(){
            if(this.title){
                return `Hi! I am ${this.name}. I work as a ${this.title}.`;
            } else{
                return super.getGreeting();
            }
        }
        hasJob(){
            return !!this.title;
        }
    }

    class Programmer extends Person{
        constructor(name, age, preferredLanguage = 'Javascript'){
            super(name,age);
            this.preferredLanguage = preferredLanguage;
        }
        getGreeting(){
            return `I am ${this.name} and my preferred language is ${this.preferredLanguage}.`;
        }
    }

    let person1 = new Employee('Gerardo', 10, 'Developer');
    console.log(person1.getGreeting());

    let person2 = new Programmer('Mike', 20, 'Assembly');
    console.log(person2.getGreeting());
});
